import React, { useReducer } from 'react';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import { reducer, handleChange } from '../Site/Utilities/UserUtil';
import { handleUpdateForm } from '../Site/Fetch_Requests/User_Fetch_Requests';

// Pseudo State
const initialState = {
    username: '',
    password: '',
    avatar: '',
}

// Responsible for User SignUp
export default function Profile(props){
    const [state, dispatch] = useReducer(reducer, initialState);
    const { currentUser, token } = props
    const { username, password, avatar } = state

    return(
        <Form onSubmit={ e => handleUpdateForm(e, state, handleLogin, token)}>
            <h1>{currentUser.username}'s Profile</h1>

            {/* Username */}
            <Form.Group controlId="username">
                <Form.Label>Change Username</Form.Label>
                <Form.Control
                    type="text" 
                    name="username" 
                    autoComplete="off" 
                    placeholder={currentUser.username} 
                    onChange={ e => handleChange(e, dispatch) }
                    />
            </Form.Group>

            {/* Avatar */}
            <img className="profileImage" src={avatar} alt={currentUser.username} />
            <Form.Group controlId="avatar">
                <Form.Label>Avatar</Form.Label>
                <Form.Control
                    type="text" 
                    name="avatar" 
                    autoComplete="off" 
                    onChange={ e => handleChange(e, dispatch) } 
                />
            </Form.Group>

            {/* Password */}
            <Form.Group controlId="Password">
                <Form.Label>Change Password</Form.Label>
                <Form.Control
                    type="password" 
                    name="password" 
                    value="password"
                    autoComplete="off" 
                    onChange={ e => handleChange(e, dispatch) }
                />
            </Form.Group>

            {/* Submit Button */}
            <Button type="submit">
                Sign Up
            </Button>
        </Form>
    )
}
