import React, { Component } from 'react';
import Phaser from 'phaser';
import phaserReact from "phaser3-react";
import { render } from 'react-dom';
import { gameConfig } from './Utilities/gameState';
// import TestScene from './Scenes/TestScene';

const config = {
    parent: 'phaser-game',
    dom: { createContainer: true },
    width: 1200,
    height: 720,
    type: engine.AUTO,
    scale: {
        parent: 'gameContainer',
        // scaleMode: engine.Scale.RESIZE,
        autoCenter: engine.Scale.CENTER_BOTH,
        // mode: engine.Scale.FIT,
        width: 1200,
        height: 720,
    },
    render: {
        antialias: false,
        pixelArt: true,
        roundPixels: true
    },
    physics: {
        default: 'arcade',
        arcade: {
            gravity: { y: 400 },
            debug: true
        }
    },
    scene: [
        BootScene
    ],
    plugins: {
        global: [
            {
                key: 'phaser-react',
                plugin: engineReact,
                start: true
            }
        ]
    },
}

class Main extends Component {
    game = new Phaser.Game(config);

    render(){
        return (
            <div className="row">
                <div className="col-md-6 col-md-offset-3" id="phaser-game" />
            </div>

        )
    }
}

export default Main
