import Phaser from 'phaser';

export default class Map {

    constructor(scene){
        // Current Scene
        this.scene = scene
        this.createMap();
    }

    createMap(){
        // Create the tile map
        this.map = this.scene.make.tilemap( { key: 'map' } );

        // Add the tileset Image to our map
        this.tiles = this.map.addTilesetImage( 'tileset_master', 'starting_area_tiles', 8, 8, 0, 0);

        // // Create our background
        // Ground
        scene.groundLayer = scene.map.createStaticLayer( 'ground', scene.tiles, 0, 0);
        scene.groundLayer.displayHeight = scene.game.config.height;
        scene.groundLayer.displayWidth = scene.game.config.width;

        // Walls
        scene.wallsLayer = scene.map.createStaticLayer( 'walls', scene.tiles, 0, 0);
        scene.wallsLayer.displayHeight = scene.game.config.height;
        scene.wallsLayer.displayWidth = scene.game.config.width;
        // scene.wallsLayer.setDepth(1);
        scene.wallsLayer.setCollisionByExclusion( [-1] );

        // Cliffs
        scene.cliffsLayer = scene.map.createStaticLayer( 'cliffs', scene.tiles, 0, 0);
        scene.cliffsLayer.displayHeight = scene.game.config.height;
        scene.cliffsLayer.displayWidth = scene.game.config.width;
        // scene.cliffsLayer.setDepth(1);
        scene.cliffsLayer.setCollisionByExclusion( [-1] );

        // House
        scene.houseLayer = scene.map.createStaticLayer( 'house', scene.tiles, 0, 0);
        scene.houseLayer.displayHeight = scene.game.config.height;
        scene.houseLayer.displayWidth = scene.game.config.width;
        // scene.houseLayer.setDepth(1);
        scene.houseLayer.setCollisionByExclusion( [-1] );

        // Props
        scene.propsLayer = scene.map.createStaticLayer( 'props', scene.tiles, 0, 0);
        scene.propsLayer.displayHeight = scene.game.config.height;
        scene.propsLayer.displayWidth = scene.game.config.width;
        // scene.propsLayer.setDepth(1);
        scene.propsLayer.setCollisionByExclusion( [-1] );

        // Update World Bounds
        this.scene.physics.world.bounds.width = this.map.widthInPixels * 2;
        this.scene.physics.world.bounds.height = this.map.heightInPixels * 2;
    }

}
