import Phaser from 'phaser';
import Player from '../Classes/Player';
// import Player from '../Player/Player';

class TestScene extends Phaser.Scene{

    init(){
        // console.log(data);
        this.cameras.main.setBackgroundColor('#EE6055');
    }

    create(){
        console.log(`this :`, this);
        this.player = new Player(this, 32, 32, 'characters', 0);
        this.cursors = this.input.keyboard.createCursorKeys();
        this.createMap();
        // this.player = new Player;
    }

    createMap(){

        // Create the tile map
        this.map = this.make.tilemap( { key: 'map' } );
        console.log(`In createMap().this.map :`, this.map.name);

        // Add the tileset Image to our map
        // this.load.image('starting_area_tiles', 's3://project-pure-beyond/Soulys/tileset_master.png');
        this.tiles = this.map.addTilesetImage( 'tileset_master', 'starting_area_tiles', 8, 8, 0, 0);
        console.log(`In createMap().this.tiles :`, this.tiles.name);

        // // Create our background
        // Ground
        this.groundLayer = this.map.createStaticLayer( 'ground', this.tiles, 0, 0);
        // console.log(`In CreateMap().this.groundLayer :`, this.groundLayer)
        this.groundLayer.displayHeight = this.game.config.height;
        this.groundLayer.displayWidth = this.game.config.width;

        // Walls
        this.wallsLayer = this.map.createStaticLayer( 'walls', this.tiles, 0, 0);
        this.wallsLayer.displayHeight = this.game.config.height;
        this.wallsLayer.displayWidth = this.game.config.width;

        // Cliffs
        this.cliffsLayer = this.map.createStaticLayer( 'cliffs', this.tiles, 0, 0);
        this.cliffsLayer.displayHeight = this.game.config.height;
        this.cliffsLayer.displayWidth = this.game.config.width;

        // House
        this.houseLayer = this.map.createStaticLayer( 'house', this.tiles, 0, 0);
        this.houseLayer.displayHeight = this.game.config.height;
        this.houseLayer.displayWidth = this.game.config.width;

        // Props
        this.propsLayer = this.map.createStaticLayer( 'props', this.tiles, 0, 0);
        this.propsLayer.displayHeight = this.game.config.height;
        this.propsLayer.displayWidth = this.game.config.width;
    }

    update(){
        // this.player.update(this.cursors);
    }
}

export default TestScene;
