import Phaser from 'phaser';

class TestScene extends Phaser.Scene{

    create(){
        this.createMap();
    }

    createMap(){
        // Create the tile map
        this.map = this.make.tilemap( { key: 'map' } );

        // Add the tileset Image to our map
        this.tiles = this.map.addTilesetImage( 'tileset_master', 'starting_area_tiles', 8, 8, 0, 0 );

        // Create our background
        // Ground
        this.groundLayer = this.map.createStaticLayer( 'ground', this.titles, 0, 0 );
        this.groundLayer.displayHeight = gameConfig.height;
        this.groundLayer.displayWidth = gameConfig.width;

        // Walls
        this.wallsLayer = this.map.createStaticLayer( 'walls', this.titles, 0, 0 );
        this.wallsLayer.displayHeight = gameConfig.height;
        this.wallsLayer.displayWidth = gameConfig.width;

        // Cliffs
        this.cliffsLayer = this.map.createStaticLayer( 'cliffs', this.titles, 0, 0 );
        this.cliffsLayer.displayHeight = gameConfig.height;
        this.cliffsLayer.displayWidth = gameConfig.width;

        // House
        this.houseLayer = this.map.createStaticLayer( 'house', this.titles, 0, 0 );
        this.houseLayer.displayHeight = gameConfig.height;
        this.houseLayer.displayWidth = gameConfig.width;

        // Props
        this.propsLayer = this.map.createStaticLayer( 'props', this.titles, 0, 0 );
        this.propsLayer.displayHeight = gameConfig.height;
        this.propsLayer.displayWidth = gameConfig.width;
    }

}

export default TestScene
