import BootScene from '../';

export function gameConfig(engine, engineReact){
    const config = {
        parent: 'gameConfig',
        dom: { createContainer: true },
        width: 1200,
        height: 720,
        type: engine.AUTO,
        scale: {
            parent: 'gameContainer',
            // scaleMode: Phaser.Scale.RESIZE,
            autoCenter: engine.Scale.CENTER_BOTH,
            // mode: Phaser.Scale.FIT,
            width: 1200,
            height: 720,
        },
        render: {
            antialias: false,
            pixelArt: true,
            roundPixels: true
        },
        physics: {
            default: 'arcade',
            arcade: {
                gravity: { y: 400 },
                debug: true
            }
        },
        scene: [
            BootScene
        ],
        plugins: {
            global: [
                {
                    key: 'phaser-react',
                    plugin: engineReact,
                    start: true
                }
            ]
        },
    }
}
