import React, { useReducer, Component } from 'react';
import { reducer, handleChange } from '../../Site/Utilities/UserUtil';
import CreateACharacter from './CreateACharacter';
import ShowUserCharacters from './ShowCharacters/ShowUserCharacters';
import PopulateCharacters from './ShowCharacters/PopulateCharacters';
// Pseudo State
// const initialState = {
//     character: {
//         characterName: "",
//         characterPhysique: "Masculine", 
//     }
// }

function CharacterViews(props){
    state = {
        characterName: "",
        characterPhysique: "Masculine",  
    }
    // console.log(props.currentUser);
    // const { currentUser } = props;

    return(
        <div className="CharactersDiv">
            { this.props.currentUser.characters.length > 0 ?
                <div className="hasCharacter">
                    <h1>Current Users Characters</h1>
                    <ShowUserCharacters currentUser={this.props.currentUser}/>
                    <h1>Create a Character</h1>
                    <CreateACharacter currentUser={this.props.currentUser} />  
                </div>
            :  
                <div className="noCharacters">
                    <h1>Create a Character</h1>
                    <CreateACharacter currentUser={this.props.currentUser} />
                </div>
            }
        </div>
    )
}

export default CharacterViews;
