class CharacterSelectScene extends Phaser.Scene{

    create (){
        this.characterSelectTitle = this.add.text(
            this.scale.width / 2, 
            this.scale.height * 0.10, 
            "Please Choose A Character", {
                fontFamily: 'Courier',
                fontSize: '48px'
        }).setOrigin(0.5);

        this.element = this.add.dom(200, 300).createFromCache('nameform');

        this.element.setPerspective(800);
        // console.log(this.element.node);
        grabCharacters()
        // this.element.addListener('click');
