    let characterPhysique = document.createElement('h2');
    let characterPhysiqueSprite = document.createElement('img');

    console.log(userCharacters);

    for(const character of userCharacters) {

        return(
            characterID.className = character.id,
            characterDiv.appendChild(characterID),
            characterID.innerHTML = `
