import Phaser from 'phaser';
import { SCENES } from '../Scenes/_Scenes';
import TestScene from './TestScene';
// import { tileJSON, tileSheets } from '../Utilities/picture_imports.js';

class BootScene extends Phaser.Scene{

    init(){
        this.cameras.main.setBackgroundColor('#DABECA');
    }

    preload(){
        this.loadMap()
        this.loadPlayer()
    }

    loadMap(){
        // console.log("Loaded Into BootScene")
        // console.log("This.game.scene -> :", this.game);

        //// Load Starting Map
        // TileMap.png
        this.load.image('starting_area_tiles', 'https://project-pure-beyond.s3.amazonaws.com/Soulys/tileset_master.png');
            // console.log("Loaded SpriteSheet");
        // TileMap JSON
        this.load.tilemapTiledJSON('map', 'https://project-pure-beyond.s3.amazonaws.com/Soulys/character_starting_map.json');
            // console.log("Loaded TileMap TiledJSON");
    }

    loadPlayer(){
        //// Load Default Player for now
        //// Later set it up so the spritesheet is dynamically loaded based on the player selection
        // Walk Left 
        this.load.spritesheet('playerWalkLeft', 'https://project-pure-beyond.s3.amazonaws.com/Soulys/Walk/Char_walk_left.png', {
            frameWidth: 16,
            frameHeight: 17
        });
        // Walk Right
        this.load.spritesheet('playerWalkRight', 'https://project-pure-beyond.s3.amazonaws.com/Soulys/Walk/Char_walk_right.png', {
            frameWidth: 16,
            frameHeight: 17
        });
        // Walk Up
        this.load.spritesheet('playerWalkUp', 'https://project-pure-beyond.s3.amazonaws.com/Soulys/Walk/Char_walk_up.png', {
            frameWidth: 16,
            frameHeight: 18
        });
        // Walk Down
        this.load.spritesheet('playerWalkDown', 'https://project-pure-beyond.s3.amazonaws.com/Soulys/Walk/Char_walk_down.png', {
            frameWidth: 16,
            frameHeight: 18
        });
    }

    create(){
            // console.log("CREATE IN BOOT SCENE")
        this.scene.add("testScene", TestScene, true);
        this.scene.start("testScene");
    }
}

export default BootScene
