import React, { Component } from 'react';
import Phaser from 'phaser';
import BootScene from './Scenes/BootScene';
import { IonPhaser } from '@ion-phaser/react';
// import TestScene from './Scenes/TestScene';


class Game extends Component {
    state ={
        initalize: true,
        game: {
            parent: 'gameContainer',
            // dom: { createContainer: true },
            width: 1200,
            height: 720,
            type: Phaser.AUTO,
            scale: {
                autoCenter: Phaser.Scale.CENTER_BOTH,
                width: 1200,
                height: 720,
            },
            render: {
                antialias: false,
                pixelArt: true,
                roundPixels: true
            },
            physics: {
                default: 'arcade',
                arcade: {
                    gravity: { y: 400 },
                    debug: true
                }
            },
            scene: [
                BootScene
            ]
        }
    }


    render(){
        const { initalize, game } = this.state
        console.log(`GAME.JS IS:`, this.state);

        return(
            <IonPhaser game={game} initialize={initalize} />
        )
    }
}

export default Game;
