import React, {useState, useEffect } from 'react';
import PopulateCharacters from './PopulateCharacters';

function ShowUserCharacters(props){
    const [ characters, setCharacters] = useState(props.currentUser.characters);

    // Variablize Characters
    // const characters = props.currentUser.characters;

    useEffect(() => {
        setCharacters(props.currentUser.characters);
    }, []);

     if(characters){
        return characters.map(character => {
            return(
                <PopulateCharacters key={character.id.toString()} characters={characters} buttonType={'view'}/>
            )
        })
    }else{
        return null;
    }
}
export default ShowUserCharacters;
