// Handles all of the Player Animations
// - Idle [u,d,l,r]
// - Walking [u,d,l,r]
// - Attack [u,d,l,r]
// - Dashing [u,d,l,r]
//// Plans for the Future:
// I need to incorporate 8-Way travel
//      [ul, su, ur]
//      [sl, idle, sr]
//      [dl, sd, dr]
// "ul: Up Left"        || "su: Straight Up"    || "up: Up Right"
// "sl: Straight Left"  || "idle: no movement"  || "sr: Straight Right"
//  "dl: Down Left"     || "sd: Straight Down"  || "dr: Down Right"
// By utilizing 8-Way Travel I'll be able to make smoother and more interactive controls

export function playerAnims(cursors, player){
    if(cursors.left.isDown){
        player.anims.play("playerWalkLeft", true);
    }else if(cursors.right.isDown){
        player.anims.play("playerWalkRight", true);
    }else if(cursors.up.isDown){
        player.anims.play("playerWalkUp", true);
    }else if(cursors.down.isDown){
        player.anims.play("playerWalkDown", true);
    }else{
        player.anims.stop();
    }
}

export function createWalkingAnims(player){
    //// Walking
    // Player Walk Left
    this.anims.create({
        key: 'playerWalkLeft',
        frames: this.anims.generateFrameNumbers("playerWalkLeft"),
        frameRate: 10,
        repeat: -1,
    });

    // Player Walk Right
    this.anims.create({
        key: 'playerWalkRight',
        frames: this.anims.generateFrameNumbers("playerWalkRight"),
        frameRate: 10,
        repeat: -1,
    });

    // Player Walk Up
    this.anims.create({
        key: 'playerWalkUp',
        frames: this.anims.generateFrameNumbers("playerWalkUp"),
        frameRate: 10,
        repeat: -1,
    });

    // Player Walk Down
    this.anims.create({
        key: 'playerWalkDown',
        frames: this.anims.generateFrameNumbers("playerWalkDown"),
        frameRate: 10,
        repeat: -1,
    });
}
