}

// Game Logic
export default function Main(props){
    console.log(props.currentUser.characters);
    let characters = props.currentUser.characters
    if (characters)
    for( const character of characters ){
        userCharacters[character.name] = character
    }

    console.log(`User Characters Array`, userCharacters);

    return(
        <div id="gameContainer">
            <IonPhaser game={GameState( Phaser, BootScene )} initialize={true} /> 
        </div>
    )
}
