import Phaser from 'phaser';
import CharacterSelectMenu from './CharacterSelectMenu';

class CharacterFormScene extends Phaser.Scene{

    preload(){
        this.load.scenePlugin({
            key: 'rexuiplugin', 
            url: 'https://raw.githubusercontent.com/rexrainbow/phaser3-rex-notes/master/dist/rexuiplugin.min.js',
            sceneKey: 'rexUI'
        });    
    }

    create() {
        var print = this.add.text(0, 0, '');

        var loginDialog = CreateLoginDialog(this, {
            x: 400,
            y: 300,
            title: 'Welcome',
            username: 'abc',
            password: '123',
        })
        .on('login', function (username, password) {
            print.text += `${username}:${password}\n`;
        })
        // .drawBounds(this.add.graphics(), 0xff0000)
        .popUp(500);

        this.add.text(0, 560, 'Click user name or password field to edit it\nClick Login button to show user name and password')
    }


    update(){

    }

}

export default CharacterFormScene;
