import Phaser from 'phaser';
import Player from '../Classes/Player';
// import Player from '../Player/Player';

class TestScene extends Phaser.Scene{

    init(){
        // console.log(data);
        this.cameras.main.setBackgroundColor('#EE6055');
    }

    create(){
        // console.log(`this :`, this.scene.systems);
        // this.player = new Player(this, 32, 32, 'playerWalkLeft', 0);
        this.createPlayer();
        this.createMap();
        // this.handleCollisionColliders();
        // this.player = new Player;

        // this.wallsLayer.setCollisionByProperty({ collides: true });
        // this.cliffsLayer.setCollisionByProperty({ collides: true });
        // this.houseLayer.setCollisionByProperty({ collides: true });
        // this.propsLayer.setCollisionByProperty({ collides: true });

        // this.handleCollisionColliders()

        // Walls
        this.physics.add.collider( this.player, this.wallsLayer  );
        // Cliffs
        this.physics.add.collider( this.player, this.cliffsLayer  );
        // House
        this.physics.add.collider( this.player, this.houseLayer  );
        // Props
        this.physics.add.collider( this.player, this.propsLayer  );
    }

    createPlayer(){
        this.player = this.physics.add.sprite(400, 400, 'playerWalkLeft', 0)
        this.player.body.setImmovable(true);
        this.cursors = this.input.keyboard.createCursorKeys();

        this.player.setCollideWorldBounds(true);

        this.player.setScale(4);
        this.player.setDepth(2);
    }

    createMap(){

        // Create the tile map
        this.map = this.make.tilemap( { key: 'map' } );
        // console.log(`In createMap().this.map :`, this.map.name);

        // Add the tileset Image to our map
        // this.load.image('starting_area_tiles', 's3://project-pure-beyond/Soulys/tileset_master.png');
        this.tiles = this.map.addTilesetImage( 'tileset_master', 'starting_area_tiles', 8, 8, 0, 0);
        // console.log(`In createMap().this.tiles :`, this.tiles.name);

        // // Create our background
        // Ground
        this.groundLayer = this.map.createStaticLayer( 'ground', this.tiles, 0, 0);
        // console.log(`In CreateMap().this.groundLayer :`, this.groundLayer)
        this.groundLayer.displayHeight = this.game.config.height;
        this.groundLayer.displayWidth = this.game.config.width;

        // Walls
        this.wallsLayer = this.map.createStaticLayer( 'walls', this.tiles, 0, 0);
        this.wallsLayer.displayHeight = this.game.config.height;
        this.wallsLayer.displayWidth = this.game.config.width;
        this.wallsLayer.setDepth(1);
        this.wallsLayer.setCollisionByExclusion( [-1] );

        // Cliffs
        this.cliffsLayer = this.map.createStaticLayer( 'cliffs', this.tiles, 0, 0);
        this.cliffsLayer.displayHeight = this.game.config.height;
        this.cliffsLayer.displayWidth = this.game.config.width;
        this.cliffsLayer.setDepth(1);
        this.cliffsLayer.setCollisionByExclusion( [-1] );

        // House
        this.houseLayer = this.map.createStaticLayer( 'house', this.tiles, 0, 0);
        this.houseLayer.displayHeight = this.game.config.height;
        this.houseLayer.displayWidth = this.game.config.width;
        this.houseLayer.setDepth(1);
        this.houseLayer.setCollisionByExclusion( [-1] );

        // Props
        this.propsLayer = this.map.createStaticLayer( 'props', this.tiles, 0, 0);
        this.propsLayer.displayHeight = this.game.config.height;
        this.propsLayer.displayWidth = this.game.config.width;
        this.propsLayer.setDepth(1);
        this.propsLayer.setCollisionByExclusion( [-1] );

    }

    // Collision Colliders
    handleCollisionColliders(){
        // Ground
        this.physics.add.collider(this.player, this.groundLayer);
        // Walls
        this.physics.add.collider( this.wallsLayer, this.player );
        // Cliffs
        this.physics.add.collider( this.cliffsLayer, this.player );
        // House
        this.physics.add.collider( this.houseLayer, this.player );
        // Props
        this.physics.add.collider( this.propsLayer, this.player );

    }

    update(){
        // this.player.update(this.cursors);
        this.player.setVelocity(0);

        if(this.cursors.left.isDown){
            this.player.setVelocityX(-160);
            // console.log(`left: ${this.player.x}`);
        }else if(this.cursors.right.isDown){
            this.player.setVelocityX(160);
            // console.log(`right: ${this.player.x}`);
        }

        if(this.cursors.up.isDown){
            this.player.setVelocityY(-160);
            // console.log(`up: ${this.player.y}`);
        }else if(this.cursors.down.isDown){
            this.player.setVelocityY(160);
            // console.log(`down ${this.player.y}`);
        }

        this.handleCollisionColliders()
    }
}

export default TestScene;
