export function gameState(props){
    const { Phaser } = props

    const initialState = {
        initialize: true,
        game: {
            width: "100%",
            height: "100%",
            type: Phaser.AUTO,
            scale: {
                scaleMode: Phaser.Scale.RESIZE,
                mode: Phaser.Scale.FIT,
            },
            render: {
                antialias: true,
                pixelart: true,
                roundPixels: true
            },
            physics: {
                default: "arcade",
                arcade: {
                    gravity: { y: 400 },
                    debug: true
                }
            },
            scene:[
                null
            ],
        }
    }

    return initialState
}
