import React, { useReducer } from 'react';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import { reducer, handleChange } from '../../Site/Utilities/UserUtil';
import { handleCharacterCreationForm } from '../../Site/Fetch_Requests/Character_Fetch_Requests';
import CreateACharacter from './CreateACharacter';
import ShowUserCharacters from './ShowUserCharacters';

// Pseudo State
const initialState = {
    character: {
        characterName: "",
        characterPhysique: "Masculine", 
    }
}

export function CharacterViews(props){
    // console.log(props.currentUser);
    const { currentUser } = props;

    return(
        <div>
            { currentUser.characters.length > 1 ?
                ShowUserCharacters(props)  
            : 
                CreateACharacter(props) 
            }
        </div>
    )
}

export default CharacterViews;
