import Phaser from 'phaser';

class TestScene extends Phaser.Scene{

    init(data){
        console.log(data.map)
        this.scene.key = "TestScene";
    }
    create(){
        // console.log("CREATE MAP")
        this.createMap();
    }

    createMap(data){
        console.log(`BOOT SCENE DATA:`, data)
        // console.log("CREATEMAP FUNCTION")
        // Create the tile map
        this.map = this.make.tilemap( { key: 'map' } );

        // Add the tileset Image to our map
        this.tiles = this.map.addTilesetImage( 'tileset_master', 'starting_area_tiles');

        // Create our background
        // Ground
        this.groundLayer = this.map.createStaticLayer( 'ground', this.titles);

        // Walls
        this.wallsLayer = this.map.createStaticLayer( 'walls', this.titles);

        // Cliffs
        this.cliffsLayer = this.map.createStaticLayer( 'cliffs', this.title);

        // House
        this.houseLayer = this.map.createStaticLayer( 'house', this.titles);

        // Props
        this.propsLayer = this.map.createStaticLayer( 'props', this.titles);
    }

}

export default TestScene;
