import React, { Component }  from 'react';
import { Switch, Route, withRouter } from "react-router-dom";
import NavBar from './components/Site/Static/NavBar/NavBar';
import SignUpForm from './components/User/SignUpForm';
import LoginForm from './components/User/LoginForm';
import Profile from './components/User/Profile';
import Home from './components/Site/home';
import Main from './components/Game/Main';
import { handleLoginPersist } from './components/Site/Fetch_Requests/User_Fetch_Requests';
import { currentUserExists } from './components/Site/Utilities/UserUtil';

class App extends Component {
  state = {
    currentUser: null,
  }

  // Logs In user
  componentDidMount(){
    if(localStorage.token){
      handleLoginPersist(`profile/autologin/`, this.handleLogin, localStorage.token)
    }
  }

  // Handles User Login
  handleLogin = currentUser => {

    if(currentUser.message){
      this.props.history.push('/login')
    }else{
      this.setState({ currentUser }, () => {
        this.props.history.push('/')
      })
    }
  }

  // Handles User Logout
  handleLogout = () => {
    // Remove USER TOKEN
    localStorage.removeItem("token");

    // Remove State
    this.setState({
      currentUser: null
    })

    this.props.history.push('/')
  }

  render(){
    const { currentUser } = this.state;
    // console.log(`currentUser:`, currentUser);
    // console.log(`Current Local Storage ID:${localStorage.token}` )

    return (
      <>
        <NavBar currentUser={ currentUserExists(currentUser) } handleLogout={this.handleLogout} />

        <Switch>

          {/* Log In Page */}
          <Route path="/login">
            <LoginForm handleLogin={this.handleLogin} currentUser={ currentUserExists(currentUser) } />
          </Route>

          {/* Sign Up Page */}
          <Route path="/SignUp">
            <SignUpForm handleLogin={this.handleLogin} currentUser={ currentUserExists(currentUser) } />
          </Route>

          {/* Profile Page */}
          <Route path="/Profile">
            <Profile currentUser={ currentUserExists(currentUser) } token={localStorage.token} handleLogout={this.handleLogout} location={this.props.location} />
          </Route>

          {/* Game Page */}
          <Route path="/Game"> {/* currentUserExists returns the currentUser name not the full return value */}  
            <Main currentUser={ currentUser ? currentUser : "Null" } token={localStorage.token} />
          </Route>

          {/* Home Page */}
          <Route exact path="/">
            <Home currentUser={ currentUserExists(currentUser) } />
          </Route>

        </Switch>
      </>
    );
  }
}

export default withRouter(App);
