import Phaser from 'phaser';

export default class Map extends Phaser.Physics.Arcade.Image{

    constructor(scene, key, tileSetName, layers = []){
        super(scene, key, tileSetName, layers = [])

        // Current Scene
        this.scene = scene;
        this.key = key;
        this.tileSetName = tileSetName;
        this.layers = layers;
        this.createMap();

        // console.log(
        //     `Scene : `, scene
        // )

        // console.log(
        //     `Key : `, key
        // )

        // console.log(
        //     `tileSetName : `, tileSetName
        // )

        // console.log(
        //     `layers : `, layers
        // )
    }

    createMap(){
        // console.log(
        //     `Scene : `, this.scene
        // )

        // console.log(
        //     `Key : `, this.key
        // )

        // console.log(
        //     `tileSetName : `, this.tileSetName
        // )

        // console.log(
        //     `layers : `, this.layers
        // )
        // Create the tile map
        this.map = this.scene.make.tilemap( { key: this.key } );

        // Add the tileset Image to our map
        this.tiles = this.map.addTilesetImage( this.tileSetName, this.tileSetName, 8, 8, 0, 0);
        console.log(this.tiles)
        console.log(this.layers)
        // // Create our background
        for(const layer of this.layers){
            this.scene.map.createStaticLayer(layer, this.tiles).setCollisionByExclusion([-1]).setDisplaySize(this.scene.game.config.height, this.scene.game.config.width);
        }


        // Update World Bounds
        // this.scene.physics.world.bounds.width = this.map.widthInPixels * 2;
        // this.scene.physics.world.bounds.height = this.map.heightInPixels * 2;
    }

}
