// Collision Colliders
export function handleCollisionColliders(scene, allLayers){

    for(layer of allLayers){
        scene.physics.add.collider()
    }
    // Ground
    scene.physics.add.collider( scene.player, scene.groundLayer );
    // Walls
    scene.physics.add.collider( scene.wallsLayer, scene.player );
    // Cliffs
    scene.physics.add.collider( scene.cliffsLayer, scene.player );
    // House
    scene.physics.add.collider( scene.houseLayer, scene.player );
    // Props
    scene.physics.add.collider( scene.propsLayer, scene.player );

}
