import Phaser from 'phaser';
import CharacterSelectMenu from './CharacterSelectMenu';

class CharacterFormScene extends Phaser.Scene{

    preload(){
        this.load.plugin('rextexteditplugin', 'https://raw.githubusercontent.com/rexrainbow/phaser3-rex-notes/master/dist/rextexteditplugin.min.js', true);
        this.load.plugin('rexbbcodetextplugin', 'https://raw.githubusercontent.com/rexrainbow/phaser3-rex-notes/master/dist/rexbbcodetextplugin.min.js', true);

    }

    create(){
        console.log(this.scene);

        let printText = this.add.rexBBCodeText(400, 300, 'abc', {
            color: 'yellow',
            fontSize: '24px',
            fixedWidth: 200,
            // fixedHeight: 80,
            backgroundColor: '#333333',
            // valign: 'center'
        })
            .setOrigin(0.5)
            .setInteractive()
            .on('pointerdown', function () {
                this.plugins.get('rextexteditplugin').edit(printText);
            }, this);

        this.add.text(0, 580, 'Click text to start editing, press enter key to stop editing')
    }



    update(){

    }

}

export default CharacterFormScene;
