import Phaser from 'phaser';
import Player from '../../../Classes/PlayerClass/Player';
import { createMap } from './CreateMap';
import { handleCollisionColliders } from './HandleCollisions';

class TestScene extends Phaser.Scene{

    init(){
        this.cameras.main.setBackgroundColor( '#EE6055' );
        this.scene.add("UiScene", TestScene, true);
        this.scene.launch('UiScene');
    }

    create(){
        // Handles Player Creation Logic
        this.createPlayer();

        // Handles Map Creation Logic
        createMap(this);

        // Handles Collision Colliders
        handleCollisionColliders(this)
    }

    createPlayer(){
        // Generates a new Instance of Player
        this.player = new Player( this, 400, 400, 'playerIdleDown' );

        // Runs Player Create Function
        this.player.create();
    }

    // Update Items that need persistence/event checks
    update(){
        this.player.update();
    }
}

export default TestScene;
