import React, { useState, useEffect } from 'react';
import Phaser from 'phaser';
import { IonPhaser } from '@ion-phaser/react';

const initialState = {
    initialize: true,
    game: {
        width: 1200,
        height: 720,
        type: Phaser.CANVAS,
        scale: { 
            mode: Phaser.Scale.FIT,
        },
        render: {
            antialias: true,
            pixelart: true,
            roundPixels: true
        },
        physics: {
            default: "arcade",
            arcade: {
                gravity: { y: 400 },
                debug: true
            }
        },
        scene: []
    }
}


function Main (props) {
    // Sets Game state and Tells IonPhaser to initialize an instance of Phaser
    const { game, initialize } = initialState; 
    // const [state, dispatch] = useReducer(reducer, initialState);

    // // Sets initial states as a destructured Object
    // const { username, password } = state

    console.log(`IN GAME::MAIN, Props :->`, props)

    return(
        <div className="game">
            <IonPhaser game={game} initialize={initialize} />
        </div>
    )
}

export default Main
