import React, { useState, useEffect } from 'react';
import Phaser from 'phaser';
import { IonPhaser } from '@ion-phaser/react';
import BootScene from './Scenes/Boot/BootScene';
import { GameState } from './Utilities/GameState';
import { PhaserInput } from '@azerion/phaser-input/build/phaser-input.js';
// import RexUIPlugin from 'phaser3-rex-plugins/templates/ui/ui-plugin.js';

// Phaser Main Top Level Functionality
// User Characters Array
export let userCharacters = [];


// Phaser Plugins
const plugins = {
    "RexUIPlugin": PhaserInput
}

// Game Logic
export default function Main(props){
    const [ characters, setCharacter ] = useState([]);

    useEffect(() => {
        setCharacter(props.currentUser.characters);
        userCharacters = characters;
    }, [props.currentUser.characters, characters])

    // console.log("User Characters: ", userCharacters);
    // console.log("Characters: ", characters);
    return(
        <div className="gameContainer">
            <IonPhaser game={GameState(Phaser, BootScene, plugins)} initialize={true} /> 
        </div>
    )

}
