import ChooseCharacter from './CharacterCreationFlow/ChooseCharacter';

function CharacterSelectMenu(){

        console.log("characters: ", userCharacters);
    return(
        <div>
            {userCharacters.length > 0 ? 
                <div>
                    {ChooseCharacter()}
                    {CreateCharacter()}
                </div>
            :
                <div>
                    {CreateCharacter()}
                </div> 
            }
        </div>
    )    

}

export default CharacterSelectMenu;
