}

// Activate Player Walking Animations Based on Last Direction Player Faced
function handleWalkingAnims(player, direction){
    switch(cursors){
        case "left":
            player.anims.play("playerWalkLeft", true);
            break;

        case "right":
            player.anims.play("playerWalkRight", true);
            break;

        case "up":
            player.anims.play("playerWalkUp", true);
            break;

        case "down":
            player.anims.play("playerWalkDown", true);
            break;

        default:
            handleIdleAnimation(player, currentDirection)
            break;
    }
}

// Active Player Idle Animations Based on Last Direction Player Faced
function handleIdleAnims(player, direction){
    switch(direction){
        case "up":
            player.anims.play(`playerIdleUp`, true);
            break;

        case "down":
            player.anims.play(`playerIdleDown`, true);
            break;

        case "right":
            player.anims.play(`playerIdleRight`, true);
            break;



        default:
            player.anims.stop()
            break;
    }
}

// Create Player Walking Animation
export function createWalkingAnims(anims){
    //// Walking
    // Player Walk Left
    anims.create({
        key: 'playerWalkLeft',
        frames: anims.generateFrameNumbers("playerWalkLeft"),
        frameRate: 10,
        repeat: -1,
    });

    // Player Walk Right
