        frameRate: 10,
        repeat: -1,
    });

    // Player Walk Down
    anims.create({
        key: 'playerWalkDown',
        frames: anims.generateFrameNumbers("playerWalkDown"),
        frameRate: 10,
        repeat: -1,
    });
}

// Create Player Idle Animation
