import React from 'react';
import Phaser from 'phaser';
import { IonPhaser } from '@ion-phaser/react';

const initialState = {
    initialize: true,
    game: {
        width: window.innerWidth * window.devicePixelRatio,
        height: window.innerHeight * window.devicePixelRatio,
        type: Phaser.CANVAS,
        // scale: { 
        //     scaleMode: Phaser.Scale.RESIZE,
        //     mode: Phaser.Scale.FIT,
        // },
        render: {
            antialias: true,
            pixelart: true,
            roundPixels: true
        },
        physics: {
            default: "arcade",
            arcade: {
                gravity: { y: 400 },
                debug: true
            }
        },
    }
}


function Main (props) {
    // Sets Game state and Tells IonPhaser to initialize an instance of Phaser
    const { game, initialize } = initialState;

    console.log(`IN GAME::MAIN, Props :->`, props)

    return(
        <div className="game">
            <IonPhaser game={game} initialize={initialize} />
        </div>
    )
}

export default Main
