export function createWalkingAnims(player){
    //// Walking
    // Player Walk Left
    player.body.create({
        key: 'playerWalkLeft',
        frames: player.body.generateFrameNumbers("playerWalkLeft"),
        frameRate: 10,
        repeat: -1,
    });

    // Player Walk Right
    player.body.create({
        key: 'playerWalkRight',
        frames: player.body.generateFrameNumbers("playerWalkRight"),
        frameRate: 10,
        repeat: -1,
    });

    // Player Walk Up
    player.body.create({
        key: 'playerWalkUp',
        frames: player.body.generateFrameNumbers("playerWalkUp"),
        frameRate: 10,
        repeat: -1,
    });

    // Player Walk Down
    player.body.create({
        key: 'playerWalkDown',
        frames: player.body.generateFrameNumbers("playerWalkDown"),
        frameRate: 10,
        repeat: -1,
    });
}
