import Phaser from 'phaser';

export default class Map {

    constructor(scene, key, tileSetName, layers = []){
        // Current Scene
        this.scene = scene
        this.createMap();
    }

    createMap(){
        // Create the tile map
        this.map = this.scene.make.tilemap( { key: this.key } );

        // Add the tileset Image to our map
        this.tiles = this.map.addTilesetImage( this.tileSetName, this.tileSetName, 8, 8, 0, 0);

        // // Create our background
        for(const layer of layers){
            this.scene.map.createStaticLayer(layer, this.tiles)
        }
        // Ground
        this.scene.groundLayer = this.scene.map.createStaticLayer( 'ground', this.scene.tiles, 0, 0);
        this.scene.groundLayer.displayHeight = this.scene.game.config.height;
        this.scene.groundLayer.displayWidth = this.scene.game.config.width;

        // Walls
        this.scene.wallsLayer = this.scene.map.createStaticLayer( 'walls', this.scene.tiles, 0, 0);
        this.scene.wallsLayer.displayHeight = this.scene.game.config.height;
        this.scene.wallsLayer.displayWidth = this.scene.game.config.width;
        // this.scene.wallsLayer.setDepth(1);
        this.scene.wallsLayer.setCollisionByExclusion( [-1] );

        // Cliffs
        this.scene.cliffsLayer = this.scene.map.createStaticLayer( 'cliffs', this.scene.tiles, 0, 0);
        this.scene.cliffsLayer.displayHeight = this.scene.game.config.height;
        this.scene.cliffsLayer.displayWidth = this.scene.game.config.width;
        // this.scene.cliffsLayer.setDepth(1);
        this.scene.cliffsLayer.setCollisionByExclusion( [-1] );

        // House
        this.scene.houseLayer = this.scene.map.createStaticLayer( 'house', this.scene.tiles, 0, 0);
        this.scene.houseLayer.displayHeight = this.scene.game.config.height;
        this.scene.houseLayer.displayWidth = this.scene.game.config.width;
        // this.scene.houseLayer.setDepth(1);
        this.scene.houseLayer.setCollisionByExclusion( [-1] );

        // Props
        this.scene.propsLayer = this.scene.map.createStaticLayer( 'props', this.scene.tiles, 0, 0);
        this.scene.propsLayer.displayHeight = this.scene.game.config.height;
        this.scene.propsLayer.displayWidth = this.scene.game.config.width;
        // this.scene.propsLayer.setDepth(1);
        this.scene.propsLayer.setCollisionByExclusion( [-1] );

        // Update World Bounds
        this.scene.physics.world.bounds.width = this.map.widthInPixels * 2;
        this.scene.physics.world.bounds.height = this.map.heightInPixels * 2;
    }

}
