import Phaser from 'phaser';

class TestScene extends Phaser.Scene{

    init(data){
        // console.log(data);
        this.cameras.main.setBackgroundColor('#AD8A64');
    }
    create(){
        // console.log("CREATE MAP")
        this.createMap();
    }

    createMap(){
        // console.log(`BOOT SCENE DATA:`, data)
        // console.log("CREATEMAP FUNCTION")

        // Create the tile map
        this.map = this.make.tilemap({ key: 'map' });
        console.log(`In createMap() :`,this.map);

        // Add the tileset Image to our map
        this.tiles = this.map.addTilesetImage( 'tileset_master', 'starting_area_tiles', 8, 8, 0, 0);
        console.log(`In createMap().this.tiles :`)

        // // Create our background
        // Ground
        this.groundLayer = this.map.createStaticLayer( 'ground', this.titles);
        // this.groundLayer.displayHeight = this.game.scene.config.height;
        // this.groundLayer.displayWidth = this.game.scene.config.width;

        // Walls
        this.wallsLayer = this.map.createStaticLayer( 'walls', this.titles);
        // this.wallsLayer.displayHeight = this.game.scene.config.height;
        // this.wallsLayer.displayWidth = this.game.scene.config.width;
        // Cliffs
        this.cliffsLayer = this.map.createStaticLayer( 'cliffs', this.title);
        // this.cliffsLayer.displayHeight = this.game.scene.config.height;
        // this.cliffsLayer.displayWidth = this.game.scene.config.width;

        // House
        this.houseLayer = this.map.createStaticLayer( 'house', this.titles);
        // this.houseLayer.displayHeight = this.game.scene.config.height;
        // this.houseLayer.displayWidth = this.game.scene.config.width;

        // Props
        this.propsLayer = this.map.createStaticLayer( 'props', this.titles);
        // this.propsLayer.displayHeight = this.game.scene.config.height;
        // this.propsLayer.displayWidth = this.game.scene.config.width;
    }
}

export default TestScene;
