import Phaser from 'phaser';
import { screenText, animateRainbowText, startButton, interactiveStartButton } from './TitleScreenText';
import handleCharacterCreationForm from '../../../Site/Fetch_Requests/Character_Fetch_Requests';

class TitleScene extends Phaser.Scene{

    create(){
        // Screen Text
        screenText(this);
        this.hsv = Phaser.Display.Color.HSVColorWheel();

        // Start Button
        startButton(this);
        interactiveStartButton(this)
    }

    titleScreen(){
        this.scene.add("UiScene", UiScene, true);
        this.scene.launch('UiScene');
    }

    update(){
        animateRainbowText(this, this.hsv);
    }
}

export default TitleScene
