import Phaser from 'phaser';

export default function TitleScreenText(scene){
    scene.titleText = scene.add.text(scene.scale.width /2, scene.scale.height /2, "TEST_NAME", {
        fontSize: '64px', fill: '#fff'    
    });

    scene.titleText.setOrigin(0.5);
}
