export function screenText(scene){
    scene.titleText = scene.add.text(
        scene.scale.width / 2,
        scene.scale.height / 2,
        "TEST_NAME", {
            fontSize: '64px', 
            fill: '#D6D5C9'
    });

    scene.titleText.setOrigin(0.5);
}

export function startText(scene){
    // console.log("Created Start Text")
    // scene.startText = scene.add.text( 
    //     scene.scale.width / 2,
    //     scene.scale.height / 2 + 50,
    //     "TEST_START", {
    //         fontSize: '32px', 
    //         fill: '#902923'
    // });
    scene.button = scene.add.image( 100, 100, 'start_button')
    scene.startButton.setInteractive();
    scene.startText.setOrigin(0.5);
}

export function startTextInter(scene){


    scene.startText.on('pointerdown', () => {
        console.log(' Pointer is Down ');
        scene.startText.style.color = "#fff";

    });

    scene.startText.on('pointerover', () => {
        // console.log(' Pointer is Over ', scene.startText.style);
        console.log(' Pointer is Over ');

    })

    scene.startText.on('pointerout', () => {
        console.log(' Pointer is Out ');

    })
}
