    const characters = props.currentUser.characters;

    if(characters){
       return (
        <Container>
            <Row className="text-primary border-3 border-super border-primary">
                {characters.map(character => {
                    return(
                        <PopulateCharacters key={character.id.toString()} characters={character} />
                    )
                })}
                <br></br>
                <EditCharacters props={props} />
