import React, { Component } from 'react';
import Phaser from 'phaser';
import BootScene from './Scenes/BootScene';
// import TestScene from './Scenes/TestScene';


class Game extends Phaser.game {
    constructor(react){
        const config = {
            parent: 'gameContainer',
            // dom: { createContainer: true },
            width: 1200,
            height: 720,
            type: Phaser.AUTO,
            scale: {
                autoCenter: Phaser.Scale.CENTER_BOTH,
                width: 1200,
                height: 720,
            },
            render: {
                antialias: false,
                pixelArt: true,
                roundPixels: true
            },
            physics: {
                default: 'arcade',
                arcade: {
                    gravity: { y: 400 },
                    debug: true
                }
            },
            scene: [
                BootScene
            ],
            // plugins: {
            //     global: [
            //         {
            //             key: 'phaser-react',
            //             plugin: phaserReact,
            //             start: true
            //         }
            //     ]
            // },
        }

        super(config)

        this.react = react
    }
}

export default Game;
