import Phaser from 'phaser';

class Player extends Phaser.Physics.Arcade.Sprite{
    constructor(scene, x, y, key, frame){
        super(scene, x, y, key, frame);

        // Set Current Player Sprites Scene to this.scene
        this.scene = scene;

        // Set Base Velocity for Player Movement Speed
        // Velocity acts as a force that pushes the player Sprite towards a direction on the x/y axis.
        // The Velocity compounds on itself and is (n^2)*(delta_time/time) exponentiative 
        this.velocity = 160;

        // Work with Phaser.Physics
        this.scene.physics.world.enable(this);

        // Set immovable if another object collides with our player
        this.setImmovable(false);

        // Collide With World Bounds
        this.body.setCollideWorldBounds(true);

        // Add the player to the scene
        this.scene.add.existing(this);

        // Scale Player Size and Depth
        this.setScale(4);
        this.setDepth(2);


        // Debug
        console.log(`scene: `, scene);
        console.log(`this.scene: `, this.scene);
        console.log(`x: `, x);
        console.log(`y: `, y);
        console.log(`key: `, key);
        console.log(`frame: `, frame);
        console.log(`this.player: `, this.player);
        console.log(`this: `, this);
        console.log(`this.body: `, this.body);
    }

    update(cursors){
        this.body.setVelocity(0);

        if(cursors.left.isDown){
            this.body.setVelocityX(-this.velocity);
            console.log("left");
        }else if(cursors.right.isDown){
            this.body.setVelocityX(this.velocity);
            console.log("right");
        }

        if(cursors.up.isDown){
            this.body.setVelocityY(-this.velocity);
            console.log("up");
        }else if(cursors.down.isDown){
            this.body.setVelocityY(this.velocity);
            console.log("down");
        }
    }


    runPlayerAnimations(cursor, player){
        if(cursors.left.isDown){
            player.anims.play("playerWalkLeft", true);
        }else if(this.cursors.right.isDown){
            this.player.anims.play("playerWalkRight", true);
        }else if(this.cursors.up.isDown){
            this.player.anims.play("playerWalkUp", true);
        }else if(this.cursors.down.isDown){
            this.player.anims.play("playerWalkDown", true);
        }else{
            this.player.anims.stop();
        }
    }
}

export default Player;
