import TestScene from '../Maps/TestMap/TestScene';
import WebFontLoader from 'webfontloader';
export function screenText(scene){

    WebFontLoader.load({
        custom: {
            families: [ 'Lora' ]
        },
        active: function ()
        {
            scene.titleText = scene.add.text(
                scene.scale.width / 2,
                scene.scale.height / 2,
                "Soulys", {
                    fontFamily: 'Lora',
                    fontSize: '64px', 
                    fill: '#D6D5C9'
            });
            // add.text(32, 32, 'The face of the\nmoon was in\nshadow.', { fontFamily: 'troika', fontSize: 80, color: '#ff0000' }).setShadow(2, 2, "#333333", 2, false, true);


