    // scene.gameTitle.setOrigin(0.5);
}

let i = 0;

export function animateRainbowText(scene, hsv){
    let top = hsv[i].color;
    let bottom = hsv[359 - i].color;

    scene.titleText.setTint(top, top, bottom, bottom);

    i += 1;

    if (i === 360)
    {
        i = 0;
    }

    // let top;
