import React, { Component }  from 'react';
import { Switch, Route, withRouter } from "react-router-dom";
import NavBar from './components/Site/Static/NavBar/NavBar';
import SignUpForm from './components/User/SignUpForm';
import LoginForm from './components/User/LoginForm';
import Home from './components/home';
import { handleLoginPersist } from './components/Site/Fetch_Requests/User_Fetch_Requests';


class App extends Component {
  state = {
    currentUser: null,
  }

  // Logs In user
  componentDidMount(){
    if(localStorage.token){
      handleLoginPersist(`autologin/`, this.handleLogin, localStorage.token)
    }
  }

  handleLogin = currentUser => {

    if(currentUser.id ){
    //   this.props.history.push('/login')
    }else{
      this.setState({ currentUser }, () => {
        this.props.history.push('/')
      })
    }
  }

  handleLogout = () => {
    // Remove USER TOKEN
    localStorage.removeItem("token");

    // Remove State
    this.setState({
      currentUser: null
    })
  }

  render(){
    const { currentUser } = this.state;
    // console.log(`currentUser:`, currentUser);
    // console.log(`Current Local Storage ID:${localStorage.userId}` )
    return (
      <div>
        <NavBar currentUser={currentUser} handleLogout={this.handleLogout} />
        <Switch>
          {/* Log In Page */}
          <Route path="/login">
            <LoginForm handleLogin={this.handleLogin} currentUser={currentUser} />
          </Route>

          {/* Sign Up Page */}
          <Route path="/SignUp">
            <SignUpForm handleLogin={this.handleLogin} currentUser={currentUser} />
          </Route>
          {/* Profile Page */}
          <Route path="/Profile">
          </Route>

          {/* Game Page */}
          <Route path="/Game">
          </Route>

          {/* Home Page */}
          <Route exact path="/">
            <Home currentUser={currentUser} />
          </Route>


        </Switch>
      </div>
    );
  }
}

export default withRouter(App);
