export function loadPlayer(scene){
    //// Load Default Player for now
    //// Later set it up so the spritesheet is dynamically loaded based on the player selection
    // Walk Left 
    scene.load.spritesheet('playerWalkLeft', 'https://project-pure-beyond.s3.amazonaws.com/Soulys/Player/Walk/Char_walk_left.png', {
        frameWidth: 16,
        frameHeight: 17
    });
    // Walk Right
    load.spritesheet('playerWalkRight', 'https://project-pure-beyond.s3.amazonaws.com/Soulys/Player/Walk/Char_walk_right.png', {
        frameWidth: 16,
        frameHeight: 17
    });
    // Walk Up
    load.spritesheet('playerWalkUp', 'https://project-pure-beyond.s3.amazonaws.com/Soulys/Player/Walk/Char_walk_up.png', {
        frameWidth: 16,
        frameHeight: 17
    });
    // Walk Down
    load.spritesheet('playerWalkDown', 'https://project-pure-beyond.s3.amazonaws.com/Soulys/Player/Walk/Char_walk_down.png', {
        frameWidth: 16,
        frameHeight: 17
    });
}
