import React, { Component } from 'react';
import Phaser from 'phaser';
import { IonPhaser } from '@ion-phaser/react';
import BootScene from './Scenes/Boot/BootScene';
// import TestScene from './Scenes/TestScene';

class Main extends Component{
    state ={
        initalize: true,
        game: {
            // parent: 'gameContainer',
            // dom: { createContainer: true },
            width: 1200,
            height: 720,
            type: Phaser.AUTO,
            scale: {
                autoCenter: Phaser.Scale.CENTER_BOTH,
                width: 1200,
                height: 720,
            },
            render: {
                antialias: true,
                pixelArt: true,
                roundPixels: true
            },
            physics: {
                default: 'arcade',
                arcade: {
                    gravity: { y: 0 },
                    debug: true
                }
            },
            scene: BootScene
        }
    }


    render(){
        const { initalize, game } = this.state
        // console.log(`GAME.JS IS:`, this.state);
        // console.log(`Characters: `, this.props.currentUser.characters);

        return(
            <div className="gameContainer">
                <IonPhaser game={game} initialize={initalize} currentCharacters={this.props.currentUser.characters} /> 
            </div>

        )
    }
}//

export default Main;
