import React, { useReducer } from 'react';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import { reducer, handleChange } from '../Site/Utilities/UserUtil';
import { handleUpdateForm } from '../Site/Fetch_Requests/User_Fetch_Requests';

// Pseudo State
const initialState = {
    avatar: ''
}

// Responsible for User SignUp
export default function Profile(props){
    const [state, dispatch] = useReducer(reducer, initialState);
    const { username, currentUser, token } = props.currentUser
    const { avatar } = state

    return(
        <Form onSubmit={handleUpdateForm}>
            <h1>{username}'s Profile</h1>

            {/* Username */}
            <Form.Group controlId="username">
                <Form.Label>Change Username</Form.Label>
                <Form.Control
                    type="text" 
                    name="username" 
                    autoComplete="off" 
                    value={username} 
                    placeholder="JohnDoe123" 
                    onChange={ e => handleChange(e, dispatch) }
                    />
            </Form.Group>

            {/* Avatar */}
            <img className="profileImage" src={avatar} alt={username} />
            <Form.Group controlId="avatar">
                <Form.Label>Avatar</Form.Label>
                <Form.Control
                    type="text" 
                    name="avatar" 
                    autoComplete="off" 
                    value={avatar} 
                    onChange={ e => handleChange(e, dispatch) } 
                />
            </Form.Group>

            {/* Password */}
            <Form.Group controlId="Password">
                <Form.Label>Change Password</Form.Label>
                <Form.Control
                    type="password" 
                    name="password" 
                    value={password} 
                    onChange={ e => handleChange(e, dispatch) }
                />
            </Form.Group>

            {/* Submit Button */}
            <Button type="submit">
                Sign Up
            </Button>
        </Form>
    )
}
