import React, { Component, useEffect, useState } from 'react';
import Phaser from 'phaser';
import { IonPhaser } from '@ion-phaser/react';
import BootScene from './Scenes/Boot/BootScene';
// import TestScene from './Scenes/TestScene';

const initialState = {
    initalize: true,
    game: {
        // parent: 'gameContainer',
        // dom: { createContainer: true },
        width: 1200,
        height: 720,
        type: Phaser.AUTO,
        scale: {
            autoCenter: Phaser.Scale.CENTER_BOTH,
            width: 1200,
            height: 720,
        },
        render: {
            antialias: true,
            pixelArt: true,
            roundPixels: true
        },
        physics: {
            default: 'arcade',
            arcade: {
                gravity: { y: 0 },
                debug: true
            }
        },
        scene: BootScene
    }
}

function Main(){
    const { game, setGame } = useState(initialState.game)
    const { initialize, setInitialize } = useState(initialState.initialize);

    return(
        <div className="gameContainer">
            <IonPhaser game={game} initialize={initialize} currentCharacters={this.props.currentUser.characters} /> 
        </div>

    )
}

export default Main;
