import Phaser from 'phaser';
import TestScene from './TestScene';
import { tileJSON, tileSheets } from '../Utilities/picture_imports.js';

const BootScene = {
    key: 'boot',
    active: true,
    init: (config) => {
        console.log('[BOOT] init', config);
    },
    preload: () => {
        console.log('[BOOT] preload');
    },
    create: (config) => {
        loadMap;
        console.log('[BOOT] create', config);
    },
    loadMap: () => {
        console.log("Loaded Into BootScene")
        console.log("This.game.scene -> :", this.game);

        this.load.spritesheet('starting_area_tiles', 's3://project-pure-beyond/Soulys/tileset_master.png');
        console.log("Loaded SpriteSheet");

        this.load.tilemapTiledJSON('map', 's3://project-pure-beyond/Soulys/character_starting_map.json');
        console.log("Loaded TileMap TiledJSON");
    },
    update: () => {
        console.log('[BOOT] update');
    }
}

