export function GameState(phaser, boot, plugin){
    return({
        width: 1200,
        height: 720,
        type: phaser.AUTO,
        scale: {
            autoCenter: phaser.Scale.CENTER_BOTH,
            width: 1200,
            height: 720,
        },
        render: {
            antialias: true,
            pixelArt: true,
            roundPixels: true
        },
        physics: {
            default: 'arcade',
            arcade: {
                gravity: { y: 0 },
                debug: true
            }
        },
        scene: boot,
        plugins: {
            global:[{
                key: 'rexInputText'
            }]
        }
    })

}
