import React from 'react';
import { characterImageBase } from '../../../Site/Utilities/UserUtil';
import { Card, Button, Col } from 'react-bootstrap';
import { handleDeleteCharacter } from '../../../Site/Fetch_Requests/Character_Fetch_Requests';

function PopulateCharacters(user){
    const character = user.characters

    return(
        <Col>
            <Card style={{ width: '14rem' }}>
                <Card.Body>
                    <Card.Title>{character.name}</Card.Title>
                    {character.physique ? <Card.Img className='physiqueImg rounded border-1 border-bottom' variant="top" src={characterImageBase(character.physique)} />: <Card.Text>Physique: is undefined</Card.Text>}
                    <Card.Text>Level: {character.level}</Card.Text>
                    <Card.Text>Current Experience: {character.current_experience}</Card.Text>
                    <Card.Body className="characterButtons border-1 border-top">
                        <Button className="ml-2">Edit</Button>
                        <Button className="ml-2" onClick={e => handleDeleteCharacter(e, )}>Delete</Button>
                    </Card.Body>
                </Card.Body>
            </Card>
        </Col>
    )
}

export default PopulateCharacters;
